import { Component, OnInit } from '@angular/core';
import { MomentService } from 'src/app/services/moment.service';
import { Moment } from 'src/app/Moment';
import { ActivatedRoute, Router } from '@angular/router';
import { environment } from 'src/environments/environment';
import { faTimes, faEdit } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-moment',
  templateUrl: './moment.component.html',
  styleUrls: ['./moment.component.css']
})
export class MomentComponent implements OnInit {
  moment?: Moment;
  baseApiUrl = environment.baseApiUrl;

  faTimes = faTimes;
  faEdit = faEdit;

  constructor(
    private momentService: MomentService, 
    private route: ActivatedRoute
    ) { }

  ngOnInit(): void {  

  //id que está na url
  const id = Number(this.route.snapshot.paramMap.get('id'));

  this.momentService
  .getMoment(id)
  .subscribe((item) => (this.moment = item.data));

  }

   async removeHandler(id: number){
    await this.momentService.removeMoment(id).subscribe()

  }

}
<div class="moment" *ngIf="moment">
    <h1>{{moment.title}}</h1>
    <img src="{{ baseApiUrl }}uploads/{{moment.image}}" alt="{{moment.title}}">
    <div class="about-moment">
        <h3>Sobre Este Momento</h3>
        <p>{{moment.description}}</p>
    </div>
    <div class="actions">
        <h3>Ações</h3>
        <a href="/moments/edit/{{moment.id}}" class="edit-btn">
            <fa-icon [icon]="faEdit"></fa-icon>Editar
        </a>
        <button class="remove-btn" (click)="removeHandler(moment.id!)">
            <fa-icon [icon]="faTimes" ></fa-icon>Excluir
        </button>
    </div>
    <!--Comentarios-->
    <div class="comments">
        <p>Comentários (0):</p>
    </div>
</div>
